(function($){'use strict';$('.contact-form').each(function(e){var $contact_form=$(e);var $contact_button=$contact_form.find('.form-submit');var contact_action='/php/contact.php';$contact_form.removeClass('hidden');$('body').on('mousemove',function(){$contact_form.attr('action',contact_action);$contact_button.first().removeAttribute('disabled');});$('body').on('touchmove',function(){$contact_form.attr('action',contact_action);$contact_button.first().removeAttribute('disabled');});$('body').on('keydown',function(e){if((e.keyCode===9)||(e.keyCode===13)){$contact_form.attr('action',contact_action);$contact_button.first().removeAttribute('disabled');}});$contact_button.on('click',function(){$contact_form.addClass('js-submitted');});if(location.search.substring(1)!==''){switch(location.search.substring(1)){case 'submitted':$('.contact-submitted').removeClass('hidden');break;case 'error':$('.contact-error').removeClass('hidden');break;}}});})(u);